function getData() {
    return (JSON.parse(JSON.stringify(require('./data.json'))));
}

let div = document.createElement("div");
div.appendChild(div);
// document.getElementById('body').appendChild(div);
const data = getData();

data.forEach(stop => {
    console.log(stop.line.id + "    " + stop.headsign);
    // div.appendChild(document.createElement("p").appendChild(stop.line.id + "    " + stop.headsign));
    stop.schedules.forEach(departure => {
        if (Number(departure.departure) > 120) {
            console.log(Math.floor(Number(departure.departure) / 60) + "mins");
        } else {
            console.log("proche");
        }
    })
});
