import StopT1 from './assets/images/png/T1.png';
import StopT2 from './assets/images/png/T2.png';
import StopT3 from './assets/images/png/T3.png';
import StopT4 from './assets/images/png/T4.png';

function getData() {
    return (JSON.parse(JSON.stringify(require('./data.json'))));
}

function getTramLine(stop) {
    if (stop.line.id == 1) {
        return (StopT1);
    }
    if (stop.line.id == 2) {
        return (StopT2);
    }
    if (stop.line.id == 3) {
        return (StopT3);
    }
    if (stop.line.id == 4) {
        return (StopT4);
    }
}

function getDepartureOutput(departure) {
    if (Number(departure.departure) > 120) {
        return(Math.floor(Number(departure.departure) / 60) + " mins")
    } else {
        return("proche");
    }
}

function attributePos(StopTime, posInBoard) {
    if (posInBoard == 0) {
        StopTime.setAttribute("class", "posOne");
    } else if (posInBoard == 1) {
        StopTime.setAttribute("class", "posTwo");
    } else if (posInBoard == 2) {
        StopTime.setAttribute("class", "posThree");
    }
}

window.onload = () => {
    let tram_index = 0;
    let tram_head_index = -1;
    let all_stop_index = 100;
    let posInBoard = 0;
    const data = getData();

    data.forEach(stop => {
        let TramLineAndHeadsignDiv = document.createElement("div");
        let TramHeadsignDiv = document.createElement("div");
        let TramIdImg = document.createElement("img");
        let TramHeadsign = document.createElement("div");

        TramLineAndHeadsignDiv.setAttribute("id", tram_index);
        document.getElementById('board').appendChild(TramLineAndHeadsignDiv);
        TramHeadsignDiv.setAttribute("id", tram_head_index);
        TramHeadsignDiv.setAttribute("class", "tram-header");
        document.getElementById(tram_index).appendChild(TramHeadsignDiv);
        TramIdImg.setAttribute("src", getTramLine(stop));
        document.getElementById(String(tram_head_index)).appendChild(TramIdImg);
        TramHeadsign.appendChild(document.createTextNode("    " + stop.headsign.toUpperCase()));
        document.getElementById(String(tram_head_index)).appendChild(TramHeadsign);

        let AllTramStopsTime = document.createElement("div");
        AllTramStopsTime.setAttribute("id", all_stop_index);
        AllTramStopsTime.setAttribute("class", "Tram-Dep");
        document.getElementById('board').appendChild(AllTramStopsTime);
        stop.schedules.forEach(departure => {
            let StopTime = document.createElement("div");
            StopTime.appendChild(document.createTextNode(getDepartureOutput(departure)));
            attributePos(StopTime, posInBoard);
            if (getDepartureOutput(departure) === "proche") {
                StopTime.setAttribute("style", "color: green;");
            }
            document.getElementById(String(all_stop_index)).appendChild(StopTime);
            posInBoard += 1;
        })
        posInBoard = 0;
        all_stop_index += 1;
        tram_index += 1;
        tram_head_index -= 1;
    });
}
