// Getting data from json and hadnling output

function getJSONData() {
    const tram = require('./data.json');
    const obj = JSON.stringify(tram);
    const data = JSON.parse(obj);
}

let data = getJSONData();
// let div = document.createElement("div");
div.className = "Headsign";
document.getElementById('board').appendChild(div);
// document.body.appendChild(div);

data.forEach(element => {
    console.log(element.headsign);
    let p = document.createElement("p");
    let text = document.createTextNode(element.line.id + " " + element.headsign);
    p.appendChild(text);
    div.appendChild(p);

    let d = document.createElement("p");
    element.schedules.forEach(element => {
        if (Number(element.departure) < 120) {
            // let string = document.createTextNode("proche");
            d.appendChild(document.createTextNode("proche      "));
        } else {
            d.appendChild(document.createTextNode(Math.trunc(Math.floor(Number(element.departure) / 60)) + "mins      "));
            // let string = document.createTextNode(Number(element.departure) / 60 + "mins");
        }
        // d.appendChild(string);
        div.appendChild(d);
        console.log(element.departure);
    })
});
