const tram = require('./data.json');
const obj = JSON.stringify(tram);
const data = JSON.parse(obj);
// var index = 0
// console.log(data);

let div = document.createElement("div");
div.className = "Headsign";
// document.querySelector(".App-board").appendChild(div);
// console.log(board);
console.log(document.querySelector(".App-board"));
// board.appendChild(div);

data.forEach(element => {
    // console.log(element.headsign);
    let p = document.createElement("p");
    let text = document.createTextNode(element.headsign);
    // p.innerHTML(element.headsign);
    p.appendChild(text);
    div.appendChild(p);
    // <p className="Headsign" top="500+index" color="pink">${element.headsign}</p>
    // index += 1;
});
