const tram = require('./data.json');
const obj = JSON.stringify(tram);
const data = JSON.parse(obj);

let div = document.createElement("div");
div.className = "Headsign";
document.body.appendChild(div);

data.forEach(element => {
    console.log(element.headsign);
    let p = document.createElement("p");
    let text = document.createTextNode(element.line.id + " " + element.headsign + "\n" + element.schedules.departure);
    p.appendChild(text);
    div.appendChild(p);
});
