import StopT1 from './assets/images/png/T1.png';
import StopT2 from './assets/images/png/T2.png';
import StopT3 from './assets/images/png/T3.png';
import StopT4 from './assets/images/png/T4.png';

function getData() {
    return (JSON.parse(JSON.stringify(require('./data.json'))));
}

function getTramLine(stop) {
    if (stop.line.id == 1) {
        return (StopT1);
    }
    if (stop.line.id == 2) {
        return (StopT2);
    }
    if (stop.line.id == 3) {
        return (StopT3);
    }
    if (stop.line.id == 4) {
        return (StopT4);
    }
}

window.onload = () => {
    let tram_index = 0;
    let tram_head_index = -1;
    const data = getData();
    let str = "";

    data.forEach(stop => {
        let head_div = document.createElement("div");
        head_div.setAttribute("id", tram_index);
        document.getElementById('board').appendChild(head_div);

        let tramHead = document.createElement("div");
        tramHead.setAttribute("id", tram_head_index);
        tramHead.setAttribute("class", "tram-header");
        document.getElementById(tram_index).appendChild(tramHead);

        let img = document.createElement("img");
        img.setAttribute("src", getTramLine(stop));
        document.getElementById(String(tram_head_index)).appendChild(img);
        let div = document.createElement("div");
        div.appendChild(document.createTextNode("    " + stop.headsign.toUpperCase()));
        document.getElementById(String(tram_head_index)).appendChild(div);

        let departures = document.createElement("div");
        departures.setAttribute("class", "tram-departures");


        stop.schedules.forEach(departure => {
            let idep
            if (Number(departure.departure) > 120) {
                str = Math.floor(Number(departure.departure) / 60) + "mins"
            } else {
                str = "proche";
            }
            departures.appendChild(document.createTextNode(str));
            document.getElementById(String(tram_index)).appendChild(departures);
        })
        tram_index += 1;
        tram_head_index -= 1;
    });
}
