import StopT1 from './assets/images/png/T1.png';
import StopT2 from './assets/images/png/T2.png';
import StopT3 from './assets/images/png/T3.png';
import StopT4 from './assets/images/png/T4.png';

function getData() {
    // console.log(JSON.parse(require('./data.json')));
    return (JSON.parse(JSON.stringify(require('./data.json'))));
}

// let div = document.createElement("div");
// div.appendChild(document.createElement("p"));
// document.getElementById('body').appendChild(div);

window.onload = () => {
    const data = getData();
    let str = "";
    console.log(data[0].line.id);

    data.forEach(stop => {
        let head_div = document.createElement("div");
        head_div.setAttribute("id", "headDiv");
        document.getElementById('board').append(head_div);

        let img = document.createElement("img");
        img.setAttribute("src", StopT1);
        img.setAttribute("width", 50);
        img.setAttribute("height", 30);
        document.getElementById('headDiv').appendChild(img);

        let div = document.createElement("div");
        div.appendChild(document.createTextNode("    " + stop.headsign));
        document.getElementById('headDiv').appendChild(div);

        let underdiv = document.createElement("div");
        underdiv.setAttribute("id", "underdiv")

        let departures = document.createElement("div");
        stop.schedules.forEach(departure => {
            if (Number(departure.departure) > 120) {
                str = Math.floor(Number(departure.departure) / 60) + "mins"
                // console.log(Math.floor(Number(departure.departure) / 60) + "mins");
            } else {
                str = "proche";
                // console.log("proche");
            }
            departures.appendChild(document.createTextNode(str));
            document.getElementById('board').appendChild(departures);
        })
    });
}
