    document.createElement("div");
    document.appendChild(document.createTextNode(stop.line.id + "    " + stop.headsign));
    document.getEleementById('board').appendChild
//     // div.appendChild(document.createElement("p").appendChild(stop.line.id + "    " + stop.headsign));
